import React, { useState } from "react";
import axios from "axios";
import { useContext } from "react";
import { AuthContext } from "../context/AuthContext";
import { useNavigate, useLocation } from "react-router-dom";

function Login({ onLogin }) {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);
  const { setAuthToken } = useContext(AuthContext); // Access setAuthToken from context
  const { setUserRole } = useContext(AuthContext);
  const navigate = useNavigate();
  const location = useLocation(); // Get the current URL and the query params

  const handleLogin = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);

    try {
      const response = await axios.post("http://localhost:5000/api/auth/login", {
        username,
        password,
      });

      const { message, token, user } = response.data;

      // Save token to context and localStorage
      setAuthToken(token);
      setUserRole(user.role);

      // Call the onLogin function (to update user state)
      onLogin(user);

      // Get the 'redirect' query parameter or default to '/'
      const redirectPath = new URLSearchParams(location.search).get("redirect") || "/";

      // Redirect the user to the original page they wanted to visit
      navigate(redirectPath);

      alert(message);
    } catch (error) {
      setError(
        error.response?.data?.message || "An error occurred. Please try again."
      );
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <form onSubmit={handleLogin}>
        <h2>Login</h2>
        {error && <p style={{ color: "red" }}>{error}</p>}
        <input
          type="text"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          placeholder="Username"
          required
        />
        <input
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          placeholder="Password"
          required
        />
        <button type="submit" disabled={loading}>
          {loading ? "Logging in..." : "Login"}
        </button>
      </form>
    </div>
  );
}

export default Login;
