import React, { useState, useEffect, useContext } from "react";
import { AuthContext } from "../context/AuthContext";
import axios from "axios";
import Calendar from "react-calendar";
import "react-calendar/dist/Calendar.css"; // Import calendar styles

function BookingList({ onSelectBooking }) {
  const [bookings, setBookings] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const { authToken } = useContext(AuthContext);
  const [selectedDate, setSelectedDate] = useState(new Date());

  useEffect(() => {
    const fetchBookings = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/bookings/", {
          headers: { Authorization: `Bearer ${authToken}` },
        });
        setBookings(response.data);
      } catch (err) {
        setError(err.response?.data?.message || "Failed to fetch bookings.");
      } finally {
        setLoading(false);
      }
    };

    fetchBookings();
  }, [authToken]);

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;

  const events = bookings.map((booking) => ({
    date: new Date(booking.check_in_date).toLocaleDateString(), // Format to match the calendar
    title: `Room ${booking.room_id} - User ${booking.user_id}`,
  }));

  // Filter events for the selected date
  const eventsOnSelectedDate = events.filter((event) =>
    event.date === selectedDate.toLocaleDateString()
  );

  return (
    <div>
      <h2>Booking Calendar</h2>
      
      {/* Simple calendar display */}
      <Calendar
        onChange={setSelectedDate}
        value={selectedDate}
      />

      {/* Display events for the selected date */}
      <h3>Events on {selectedDate.toLocaleDateString()}:</h3>
      <ul>
        {eventsOnSelectedDate.length > 0 ? (
          eventsOnSelectedDate.map((event, index) => (
            <li key={index}>
              {event.title}
            </li>
          ))
        ) : (
          <li>No bookings for this date</li>
        )}
      </ul>
    </div>
  );
}

export default BookingList;
