import React, { useEffect, useState } from "react";
import axios from "axios";

function RoomList() {
  const [rooms, setRooms] = useState([]);
  const [filteredRooms, setFilteredRooms] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Filters
  const [priceRange, setPriceRange] = useState([0, 1000]);
  const [roomType, setRoomType] = useState("");

  useEffect(() => {
    const fetchRooms = async () => {
      setLoading(true);
      setError(null);
  
      try {
        const response = await axios.get("http://localhost:5000/api/rooms/"); // Change to GET request
        setRooms(response.data.rooms || []); // Set rooms dynamically
      } catch (err) {
        console.error("Error fetching rooms:", err);
        setError("Failed to fetch available rooms. Please try again later.");
      } finally {
        setLoading(false);
      }
    };
  
    fetchRooms();
  }, []);

  // Apply filters to the rooms
  useEffect(() => {
    const applyFilters = () => {
      let filtered = rooms;

      // Filter by price range
      filtered = filtered.filter(
        (room) => Number(room.price) >= priceRange[0] && Number(room.price) <= priceRange[1]
      );

      // Filter by room type
      if (roomType) {
        filtered = filtered.filter((room) => room.type === roomType);
      }

      setFilteredRooms(filtered);
      console.log("Filtered Rooms:", filtered);
    };

    applyFilters();
  }, [rooms, priceRange, roomType]);

  if (loading) return <div>Loading rooms...</div>;
  if (error) return <div style={{ color: "red" }}>{error}</div>;

  return (
    <div>
      <h2>Available Rooms</h2>

      {/* Filter Controls */}
      <div>
        <h3>Filters</h3>

        <div>
          <label>Price Range:</label>
          <input
            type="number"
            placeholder="Min"
            value={priceRange[0]}
            onChange={(e) =>
              setPriceRange([Number(e.target.value), priceRange[1]])
            }
          />
          <input
            type="number"
            placeholder="Max"
            value={priceRange[1]}
            onChange={(e) =>
              setPriceRange([priceRange[0], Number(e.target.value)])
            }
          />
        </div>

        <div>
          <label>Room Type:</label>
          <select
            value={roomType}
            onChange={(e) => setRoomType(e.target.value)}
          >
            <option value="">All</option>
            <option value="Single">Single</option>
            <option value="Double">Double</option>
            <option value="Suite">Suite</option>
          </select>
        </div>
      </div>

      {/* Room List */}
      {filteredRooms.length === 0 ? (
        <p>No rooms match the selected criteria.</p>
      ) : (
        <ul>
          {filteredRooms.map((room) => (
            <li key={room.number}>
              <strong>{room.type}</strong>: ${room.price} per night
              <br />
              Description: {room.description}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default RoomList;
