import React, { useState } from "react";
import RoomForm from "../components/RoomForm";
import BookingList from "../components/BookingList";
import BookingUpdate from "../components/BookingUpdate";

function AdminPage() {
  const [selectedBooking, setSelectedBooking] = useState(null);

  const handleSelectBooking = (booking) => {
    setSelectedBooking(booking);
  };

  return (
    <div>
      <h1>Admin Dashboard</h1>

      {/* Room management form */}
      <RoomForm />

      {/* Display list of all bookings and allow selection */}
      <h2>All Bookings</h2>
      <BookingList onSelectBooking={handleSelectBooking} />

      {/* Update booking if one is selected */}
      {selectedBooking && (
        <BookingUpdate booking={selectedBooking} onUpdate={() => setSelectedBooking(null)} />
      )}
    </div>
  );
}

export default AdminPage;
