import React, { useEffect, useState } from "react";
import axios from "axios";

function RoomList() {
  const [rooms, setRooms] = useState([]); // Complete list of rooms
  const [filteredRooms, setFilteredRooms] = useState([]); // Rooms after filtering
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Filters
  const [priceRange, setPriceRange] = useState([0, 1000]); // Default price range
  const [roomType, setRoomType] = useState(""); // Room type filter
  const [features, setFeatures] = useState({
    seaView: false,
    freeBreakfast: false,
  }); // Additional features filter

  useEffect(() => {
    // Fetch room data from the backend
    const fetchRooms = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/rooms/");
        const roomData = response.data|| [];
        setRooms(roomData);
        setFilteredRooms(roomData); // Initial display
      } catch (err) {
        console.error("Error fetching rooms:", err);
        setError("Failed to load rooms.");
      } finally {
        setLoading(false);
      }
    };

    fetchRooms();
  }, []);

  // Apply filters to the rooms
  useEffect(() => {
    const applyFilters = () => {
      let filtered = rooms;

      // Filter by price range
      filtered = filtered.filter(
        (room) => room.price >= priceRange[0] && room.price <= priceRange[1]
      );

      // Filter by room type
      if (roomType) {
        filtered = filtered.filter((room) => room.type === roomType);
      }

      // Filter by additional features
      if (features.seaView) {
        filtered = filtered.filter((room) =>
          room.features.includes("sea view")
        );
      }
      if (features.freeBreakfast) {
        filtered = filtered.filter((room) =>
          room.features.includes("free breakfast")
        );
      }

      setFilteredRooms(filtered);
    };

    applyFilters();
  }, [rooms, priceRange, roomType, features]); // Reapply filters whenever criteria change

  if (loading) return <div>Loading rooms...</div>;
  if (error) return <div style={{ color: "red" }}>{error}</div>;

  return (
    <div>
      <h2>Available Rooms</h2>

      {/* Filter Controls */}
      <div>
        <h3>Filters</h3>

        {/* Price Range */}
        <div>
          <label>Price Range:</label>
          <input
            type="number"
            placeholder="Min"
            value={priceRange[0]}
            onChange={(e) =>
              setPriceRange([Number(e.target.value), priceRange[1]])
            }
          />
          <input
            type="number"
            placeholder="Max"
            value={priceRange[1]}
            onChange={(e) =>
              setPriceRange([priceRange[0], Number(e.target.value)])
            }
          />
        </div>

        {/* Room Type */}
        <div>
          <label>Room Type:</label>
          <select
            value={roomType}
            onChange={(e) => setRoomType(e.target.value)}
          >
            <option value="">All</option>
            <option value="Single">Single</option>
            <option value="Double">Double</option>
            <option value="Suite">Suite</option>
          </select>
        </div>

        {/* Additional Features */}
        <div>
          <label>
            <input
              type="checkbox"
              checked={features.seaView}
              onChange={(e) =>
                setFeatures({ ...features, seaView: e.target.checked })
              }
            />
            Sea View
          </label>
          <label>
            <input
              type="checkbox"
              checked={features.freeBreakfast}
              onChange={(e) =>
                setFeatures({ ...features, freeBreakfast: e.target.checked })
              }
            />
            Free Breakfast
          </label>
        </div>
      </div>

      {/* Room List */}
      {filteredRooms.length === 0 ? (
        <p>No rooms match the selected criteria.</p>
      ) : (
        <ul>
          {filteredRooms.map((room) => (
            <li key={room.id}>
              <strong>{room.type}</strong>: ${room.price} per night
              <br />
              Features: {room.features.join(", ")}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default RoomList;
