import React, { useState, useContext } from "react";  
import { AuthContext } from "../context/AuthContext"; 
import axios from "axios";

function RoomList({ onRoomSelect }) {
  const [rooms, setRooms] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [check_in_date, setcheck_in_date] = useState("");
  const [check_out_date, setcheck_out_date] = useState("");

  // Filter states
  const [minPrice, setMinPrice] = useState("");
  const [maxPrice, setMaxPrice] = useState("");
  const [roomType, setRoomType] = useState("");

  // Access the authToken from context
  const { authToken } = useContext(AuthContext);

  // Fetch rooms with applied filters
  const fetchRooms = async () => {
    if (!check_in_date || !check_out_date) {
      setError("Please select both check-in and check-out dates.");
      return;
    }

    setLoading(true);
    setError(null);
    try {
      const response = await axios.get("http://localhost:5000/api/rooms", {
        params: {
          check_in_date: check_in_date,
          check_out_date: check_out_date,
          minPrice: minPrice || undefined, // Only pass if minPrice is set
          maxPrice: maxPrice || undefined, // Only pass if maxPrice is set
          roomType: roomType || undefined, // Only pass if roomType is set
        },
        headers: {
          Authorization: `Bearer ${authToken}`, // Include authToken in the request header
        },
      });
      setRooms(response.data);
    } catch (err) {
      console.error("Error fetching rooms:", err);
      setError("Failed to load rooms.");
    } finally {
      setLoading(false);
    }
  };

  // Filter rooms by the given criteria (price and type)
  const handleFilter = () => {
    fetchRooms(); // Apply the filter and fetch the rooms
  };

  if (loading) return <div>Loading rooms...</div>;
  if (error) return <div style={{ color: "red" }}>{error}</div>;

  return (
    <div>
      <h2>Available Rooms</h2>

      {/* Date Inputs */}
      <div>
        <label>Check-In Date:</label>
        <input
          type="date"
          value={check_in_date}
          onChange={(e) => setcheck_in_date(e.target.value)}
        />
        <label>Check-Out Date:</label>
        <input
          type="date"
          value={check_out_date}
          onChange={(e) => setcheck_out_date(e.target.value)}
        />
        <button onClick={fetchRooms}>Search</button>
      </div>

      {/* Filter Inputs */}
      <div style={{ marginTop: "20px" }}>
        <h3>Filters</h3>
        <label>Price Range:</label>
        <div>
          <input
            type="number"
            value={minPrice}
            onChange={(e) => setMinPrice(e.target.value)}
            placeholder="Min Price"
          />
          <input
            type="number"
            value={maxPrice}
            onChange={(e) => setMaxPrice(e.target.value)}
            placeholder="Max Price"
          />
        </div>
        <div>
          <label>Room Type:</label>
          <input
            type="text"
            value={roomType}
            onChange={(e) => setRoomType(e.target.value)}
            placeholder="Room Type"
          />
        </div>
        <button onClick={handleFilter}>Apply Filters</button>
      </div>

      {/* Room List */}
      {rooms.length === 0 ? (
        <p>No available rooms for the selected dates and filters.</p>
      ) : (
        <ul>
          {rooms.map((room) => (
            <li
              key={room.room_id}
              onClick={() => onRoomSelect(room)} // Invoke callback with the selected room
              style={{ cursor: "pointer", marginBottom: "10px" }}
            >
              <strong>{room.type}</strong>: ${room.price} per night
              <br />
              Description: {room.description}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default RoomList;
