// src/components/RoomForm.js
import React, {useEffect, useState } from "react";

function RoomForm({ authToken }) {
  const [formData, setFormData] = useState({
    type: "",
    number: "",
    price: "",
    description: "",
    imageUrl: "",
  });

  const [message, setMessage] = useState(null);
  const [error, setError] = useState(null);

  // Handle form field changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage(null);
    setError(null);

    try {
      const response = await axios.post("http://localhost:5000/api/rooms/", formData, {
        headers: {
          Authorization: `Bearer ${authToken}`, // Assuming token is stored in localStorage
        },
      });
      setMessage(response.data.message);
      setFormData({ type: "", number: "", price: "", description: "", imageUrl: "" });
    } catch (err) {
      setError("Failed to create room. Please try again.");
      console.error(err);
    }
  };
  return (
    <div>
      Room Form - Create Room Here
    </div>
  );
}

export default RoomForm;
