// src/pages/HomeDashboard.js
import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

function HomeDashboard({ user }) {
  const navigate = useNavigate();
  const [role, setRole] = useState(null);

  const userLinks = [
    { name: "Book a Room", path: "/user" },
    { name: "My Bookings", path: "/user" },
    { name: "Update Profile", path: "/user" },
  ];

  const adminLinks = [
    { name: "Manage Rooms", path: "/admin" },
    { name: "Manage Bookings", path: "/admin" },
    { name: "View Reports", path: "/admin" },
  ];

  const roomAvailability = [
    { type: "Single Room", available: 5 },
    { type: "Double Room", available: 3 },
    { type: "Suite", available: 2 },
  ];

  useEffect(() => {
    if (user && user.role) {
      setRole(user.role);
    }
  }, [user]);

  const handleNavigation = (path) => {
    navigate(path);
  };

  return (
    <div className="home-dashboard min-h-screen bg-gray-100 p-6">
      <div className="container mx-auto max-w-4xl bg-white shadow-lg rounded-lg p-8">
        <h1 className="text-2xl font-bold text-gray-800">
          Welcome, {user ? user.username : "Guest"}!
        </h1>
        <p className="text-gray-600">
          You are logged in as {role || "a User or Admin"}.
        </p>

        <section className="quick-links mt-6">
          <h2 className="text-xl font-semibold text-gray-700 mb-4">
            Quick Links
          </h2>
          <ul className="grid grid-cols-1 md:grid-cols-2 gap-4">
            {(!role || role === "user") &&
              userLinks.map((link, index) => (
                <li key={index}>
                  <button
                    onClick={() => handleNavigation(link.path)}
                    className="w-full py-2 px-4 bg-blue-500 hover:bg-blue-600 text-white font-medium rounded-lg shadow-md transition duration-300"
                  >
                    {link.name}
                  </button>
                </li>
              ))}
            {(!role || role === "admin") &&
              adminLinks.map((link, index) => (
                <li key={index}>
                  <button
                    onClick={() => handleNavigation(link.path)}
                    className="w-full py-2 px-4 bg-green-500 hover:bg-green-600 text-white font-medium rounded-lg shadow-md transition duration-300"
                  >
                    {link.name}
                  </button>
                </li>
              ))}
          </ul>
        </section>

        <section className="room-availability mt-8">
          <h2 className="text-xl font-semibold text-gray-700 mb-4">
            Room Availability
          </h2>
          <ul className="space-y-2">
            {roomAvailability.map((room, index) => (
              <li
                key={index}
                className="flex justify-between p-4 bg-gray-100 rounded-lg shadow-sm"
              >
                <span className="text-gray-800 font-medium">{room.type}</span>
                <span className="text-gray-600">
                  {room.available} available
                </span>
              </li>
            ))}
          </ul>
        </section>

        <section className="notifications mt-8">
          <h2 className="text-xl font-semibold text-gray-700 mb-4">
            Notifications
          </h2>
          <p className="text-gray-600 bg-gray-100 p-4 rounded-lg shadow-sm">
            No new notifications.
          </p>
        </section>

        <button
          onClick={() => navigate("/logout")}
          className="mt-8 w-full py-2 px-4 bg-red-500 hover:bg-red-600 text-white font-medium rounded-lg shadow-md transition duration-300"
        >
          Logout
        </button>
      </div>
    </div>
  );
}

export default HomeDashboard;
