import React, { useEffect, useState, useContext } from "react";
import { AuthContext } from "../context/AuthContext";
import axios from "axios";
import FullCalendar from "@fullcalendar/react";
import dayGridPlugin from "@fullcalendar/daygrid";

// Import the correct CSS for FullCalendar v5
import '@fullcalendar/core/main.css';
import '@fullcalendar/daygrid/main.css';

function BookingList({ onSelectBooking }) {
  const [bookings, setBookings] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const { authToken } = useContext(AuthContext);

  useEffect(() => {
    const fetchBookings = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/bookings/", {
          headers: { Authorization: `Bearer ${authToken}` },
        });
        setBookings(response.data);
      } catch (err) {
        setError(err.response?.data?.message || "Failed to fetch bookings.");
      } finally {
        setLoading(false);
      }
    };
    fetchBookings();
  }, [authToken]);

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;

  const events = bookings.map((booking) => ({
    id: booking.id,
    title: `Room ${booking.room_id} - User ${booking.user_id}`,
    start: booking.check_in_date,
    end: booking.check_out_date,
    allDay: true,
  }));

  return (
    <div>
      <h2>Booking Calendar</h2>
      <FullCalendar
        plugins={[dayGridPlugin]}
        initialView="dayGridMonth"
        events={events}
        height="auto"
        eventClick={({ event }) => {
          const selectedBooking = bookings.find((b) => b.id === event.id);
          onSelectBooking(selectedBooking);
        }}
      />
    </div>
  );
}

export default BookingList;
