import React, { useState, useContext } from "react";
import axios from "axios";
import { AuthContext } from "../context/AuthContext";

function BookingUpdate({ booking, onUpdate }) {
  const [updatedBooking, setUpdatedBooking] = useState(booking);

  // Access the authToken from AuthContext
  const { authToken } = useContext(AuthContext);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setUpdatedBooking((prev) => ({ ...prev, [name]: value }));
  };

  const handleUpdate = async () => {
    try {
      // Axios PUT request with Authorization header
      await axios.put(
        `http://localhost:5000/api/bookings/${booking.id}`,
        updatedBooking,
        {
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${authToken}`,
          },
        }
      );
      alert("Booking updated successfully!");
      onUpdate(); // Notify parent of update
    } catch (error) {
      alert(
        "Error updating booking: " +
          (error.response?.data?.message || error.message)
      );
    }
  };

  const handleDelete = async () => {
    try {
      // Axios DELETE request with Authorization header
      await axios.delete(`http://localhost:5000/api/bookings/${booking.id}`, {
        headers: {
          Authorization: `Bearer ${authToken}`,
        },
      });
      alert("Booking deleted successfully!");
      onUpdate(); // Notify parent of deletion
    } catch (error) {
      alert(
        "Error deleting booking: " +
          (error.response?.data?.message || error.message)
      );
    }
  };

  return (
    <div>
      <h2>Update Booking</h2>
      <label>
        Customer Name:
        <input
          type="text"
          name="customerName"
          value={updatedBooking.customerName}
          onChange={handleInputChange}
        />
      </label>
      <label>
        Date:
        <input
          type="date"
          name="date"
          value={updatedBooking.date}
          onChange={handleInputChange}
        />
      </label>
      <label>
        Status:
        <select
          name="status"
          value={updatedBooking.status}
          onChange={handleInputChange}
        >
          <option value="booked">Booked</option>
          <option value="available">Available</option>
        </select>
      </label>
      <div>
        <button onClick={handleUpdate}>Update Booking</button>
        <button onClick={handleDelete}>Delete Booking</button>
      </div>
    </div>
  );
}

export default BookingUpdate;
