import React, { useEffect, useState, useContext } from "react";
import { AuthContext } from "../context/AuthContext";
import axios from "axios";
import FullCalendar from "@fullcalendar/react"; 
import dayGridPlugin from "@fullcalendar/daygrid";
import "@fullcalendar/core/main.css";
import "@fullcalendar/daygrid/main.css";

function BookingList({ onSelectBooking }) {
  const [bookings, setBookings] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Access the authToken from AuthContext
  const { authToken } = useContext(AuthContext);

  useEffect(() => {
    const fetchBookings = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/bookings/", {
          headers: { Authorization: `Bearer ${authToken}` }
        });

        setBookings(response.data);
      } catch (err) {
        setError(err.response?.data?.message || "Failed to fetch bookings.");
      } finally {
        setLoading(false);
      }
    };

    fetchBookings();
  }, [authToken]);

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;

  // Format bookings for FullCalendar events
  const events = bookings.map((booking) => ({
    id: booking.id,
    title: `${booking.customerName} - ${booking.status}`,
    start: booking.date,
    allDay: true
  }));

  return (
    <div>
      <h2>Booking List</h2>
      <ul>
        {bookings.map((booking) => (
          <li key={booking.id}>
            <strong>{booking.customerName}</strong> - {booking.date} - {booking.status}
            <button onClick={() => onSelectBooking(booking)}>Edit</button>
          </li>
        ))}
      </ul>

      <h2>Booking Calendar</h2>
      <FullCalendar
        plugins={[dayGridPlugin]}
        initialView="dayGridMonth"
        events={events}
        height="auto"
        eventClick={({ event }) => {
          const selectedBooking = bookings.find(
            (booking) => booking.id === event.id
          );
          onSelectBooking(selectedBooking);
        }}
      />
    </div>
  );
}

export default BookingList;
