import "./styles.css";
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import { useContext } from "react";
import { AuthContext } from "./context/AuthContext"; // Import AuthContext to access authentication state
import HomePage from "./pages/HomePage";
import AdminPage from "./pages/AdminPage";
import UserPage from "./pages/UserPage";
import UpdateUserPage from "./pages/UpdateUserProfile";
import Login from "./components/Login"; // Import Login Component

export default function App() {
  const { authToken, userRole } = useContext(AuthContext); // Get authToken and userRole from context

  // PrivateRoute Component - Protects routes that need authentication
  const PrivateRoute = ({ element, requiredRole }) => {
    if (!authToken) {
      // If no authToken (not logged in), redirect to Login page
      return <Login />;
    }

    // If the user is authenticated but lacks the required role
    if (requiredRole && userRole !== requiredRole) {
      alert("Access Denied. You do not have permission to view this page.");
      return <HomePage />;
    }

    // If user is authenticated and has the correct role, render the element
    return element;
  };

  return (
    <div>
      <Router>
        <Routes>
          {/* Public Routes */}
          <Route path="/" element={<HomePage />} />
          <Route path="/login" element={<Login />} />

          {/* Protected Routes */}
          <Route
            path="/admin"
            element={<PrivateRoute element={<AdminPage />} requiredRole="admin" />}
          />
          <Route
            path="/user"
            element={<PrivateRoute element={<UserPage />} requiredRole="user" />}
          />
          <Route
            path="/user/update"
            element={<PrivateRoute element={<UpdateUserPage />} requiredRole="user" />}
          />

          {/* Catch-all route for undefined paths */}
          <Route path="*" element={<HomePage />} />
        </Routes>
      </Router>
    </div>
  );
}
