import React, { useState } from "react";
import axios from "axios";
import { useContext } from "react";
import { AuthContext } from "../context/AuthContext"; // Import the AuthContext
import { useNavigate } from "react-router-dom"; // Import useNavigate for redirection

function Login({ onLogin }) {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState(null); // State to handle errors
  const [loading, setLoading] = useState(false); // State to handle loading
  const { setAuthToken } = useContext(AuthContext); // Access setAuthToken from context
  const navigate = useNavigate(); // To navigate after login

  const handleLogin = async (e) => {
    e.preventDefault();
    setLoading(true); // Start loading
    setError(null); // Reset error

    try {
      const response = await axios.post("http://localhost:5000/api/auth/login", {
        username,
        password,
      });

      const { message, token, user } = response.data; // Extract user details and token

      // Save token to localStorage and context state
      setAuthToken(token); // Update the token in context (automatically updates localStorage as well)

      // Call the onLogin function with the user object (to update HomePage state)
      onLogin(user);  // This updates the user state in the HomePage

      // After successful login, navigate to HomePage or dashboard
      navigate("/"); // Navigate to the HomePage or Dashboard

      alert(message); // Notify user of success
    } catch (error) {
      // Handle error response
      setError(
        error.response?.data?.message || "An error occurred. Please try again."
      );
    } finally {
      setLoading(false); // End loading
    }
  };

  return (
    <div className="login-container">
      <form onSubmit={handleLogin} className="login-form">
        <h2>Login</h2>
        {error && <p style={{ color: "red" }}>{error}</p>} {/* Display error */}
        
        <div>
          <input
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            placeholder="Username"
            required
          />
        </div>
        
        <div>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            placeholder="Password"
            required
          />
        </div>

        <div>
          <button type="submit" disabled={loading}>
            {loading ? "Logging in..." : "Login"}
          </button>
        </div>
      </form>
    </div>
  );
}

export default Login;
