import React, { useState, useEffect } from "react";
import axios from "axios";

function RoomBooking({ user }) {
  const [rooms, setRooms] = useState([]); // Dynamic list of rooms
  const [selectedRoom, setSelectedRoom] = useState(null);
  const [checkInDate, setCheckInDate] = useState("");
  const [checkOutDate, setCheckOutDate] = useState("");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null); // Error state for fetching or booking issues

  useEffect(() => {
    const fetchRooms = async () => {
      setLoading(true);
      setError(null);
  
      try {
        const response = await axios.get("http://localhost:5000/api/rooms/"); // Change to GET request
        setRooms(response.data.rooms || []); // Set rooms dynamically
      } catch (err) {
        console.error("Error fetching rooms:", err);
        setError("Failed to fetch available rooms. Please try again later.");
      } finally {
        setLoading(false);
      }
    };
  
    fetchRooms();
  }, []);

  const handleRoomSelection = (e) => {
    const roomId = parseInt(e.target.value);
    const room = rooms.find((room) => room.id === roomId);
    setSelectedRoom(room || null); // Set room or null if no valid selection
  };

  const handleBook = async () => {
    // Input validation
    if (!selectedRoom || !checkInDate || !checkOutDate) {
      setError("Please select a room and both check-in and check-out dates.");
      return;
    }

    if (new Date(checkInDate) >= new Date(checkOutDate)) {
      setError("Check-out date must be after check-in date.");
      return;
    }

    setError(null); // Clear any previous errors
    setLoading(true);

    try {
      const bookingData = {
        username: user.username,
        roomType: selectedRoom.type,
        roomPrice: selectedRoom.price,
        checkInDate,
        checkOutDate,
      };

      const response = await axios.post("http://localhost:5000/api/bookings", {
        action: "bookRoom",
        ...bookingData,
      });

      alert(`Room booked successfully! Booking ID: ${response.data.bookingId}`);
      // Reset form
      setSelectedRoom(null);
      setCheckInDate("");
      setCheckOutDate("");
    } catch (error) {
      console.error("Error booking room:", error);
      setError(
        error.response?.data?.message ||
          "Failed to book room. Please try again."
      );
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <h2>Book a Room</h2>
      {error && <p style={{ color: "red" }}>{error}</p>} {/* Display errors */}
      {loading && <p>Loading rooms...</p>} {/* Loading state */}
      {!loading && rooms.length > 0 && (
        <>
          <select onChange={handleRoomSelection} value={selectedRoom?.id || ""}>
            <option value="">Select a room type</option>
            {rooms.map((room) => (
              <option key={room.id} value={room.id}>
                {room.type} - ${room.price} per night
              </option>
            ))}
          </select>
          <br />
          <input
            type="date"
            value={checkInDate}
            onChange={(e) => setCheckInDate(e.target.value)}
            placeholder="Check-in date"
          />
          <input
            type="date"
            value={checkOutDate}
            onChange={(e) => setCheckOutDate(e.target.value)}
            placeholder="Check-out date"
          />
          <br />
          <button onClick={handleBook} disabled={loading}>
            {loading ? "Booking..." : "Book Now"}
          </button>
        </>
      )}
      {!loading && rooms.length === 0 && (
        <p>No rooms available at the moment.</p>
      )}
    </div>
  );
}

export default RoomBooking;
