import React, { useEffect, useState, useContext } from "react";
import { AuthContext } from "../context/AuthContext";
import axios from "axios";

function BookingList({ onSelectBooking }) {
  const [bookings, setBookings] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Access the authToken from AuthContext
  const { authToken } = useContext(AuthContext);

  useEffect(() => {
    const fetchBookings = async () => {
      try {
        // Axios GET request with Authorization header
        const response = await axios.get("http://localhost:5000/api/bookings/", {
          headers: {
            Authorization: `Bearer ${authToken}`,
          },
        });

        // Set the bookings data from the response
        setBookings(response.data);
      } catch (err) {
        // Handle errors and set error state
        setError(err.response?.data?.message || "Failed to fetch bookings.");
      } finally {
        // Ensure loading is set to false after the request
        setLoading(false);
      }
    };

    fetchBookings();
  }, [authToken]); // Add authToken as dependency

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;

  return (
    <div>
      <ul>
        {bookings.map((booking) => (
          <li key={booking.id}>
            <strong>{booking.customerName}</strong> - {booking.date} - {booking.status}
            <button onClick={() => onSelectBooking(booking)}>Edit</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default BookingList;
