import React, { useState } from "react"; 
import { useNavigate } from "react-router-dom";
import Registration from "../components/Registration";
import Login from "../components/Login";
import RoomBooking from "../components/RoomBooking";
import RoomList from "../components/RoomList";
import BookingHistory from "../components/BookingHistory";

function UserPage() {
  const [user, setUser] = useState(null);
  const [isRegistering, setIsRegistering] = useState(false);
  const [selectedRoom, setSelectedRoom] = useState(null); // State to track selected room
  
  const { authToken, userRole } = useContext(AuthContext);
  const navigate = useNavigate();

  useEffect(() => {
    if (!authToken || userRole !== "user") {
      navigate("/login?redirect=/user");
    }
  }, [authToken, userRole, navigate]);

  const handleRegister = () => setIsRegistering(false);
  const handleLogin = (loggedInUser) => setUser(loggedInUser);

  return (
    <div>
      <h1>User Portal</h1>
      {user ? (
        <div>
          <h2>Welcome, {user.username}!</h2>
          {/* Pass selectedRoom to RoomBooking */}
          <RoomBooking user={user} selectedRoom={selectedRoom} authToken={user.authToken} />
          {/* Pass setSelectedRoom to RoomList */}
          <RoomList onRoomSelect={setSelectedRoom} authToken={user.authToken}/>
          <BookingHistory user={user} />
        </div>
      ) : isRegistering ? (
        <Registration onRegister={handleRegister} />
      ) : (
        <Login onLogin={handleLogin} />
      )}
      {!user && (
        <button onClick={() => setIsRegistering(!isRegistering)}>
          {isRegistering ? "Back to Login" : "Register"}
        </button>
      )}
      <button onClick={() => navigate(-1)} style={{ marginTop: "10px" }}>
        Back to Previous Page
      </button>
    </div>
  );
}

export default UserPage;
