// src/pages/UpdateUserPage.js
import React, { useState, useEffect, useContext } from "react";
import { AuthContext } from "../context/AuthContext";
import axios from "axios";

function UpdateUserProfile() {
  const [user, setUser] = useState(null);
  const [updatedUser, setUpdatedUser] = useState(null);
  const [isEditing, setIsEditing] = useState(false);

  const { userId } = useContext(AuthContext);

  // Fetch user data when component mounts
  useEffect(() => {
    const fetchUser = async () => {
      try {
        const response = await axios.get(
          `http://localhost:5000/api/auth/user/${Number(userId)}`
        );
        setUser(response.data);
        setUpdatedUser(response.data);
      } catch (error) {
        console.error("Error fetching user data:", error);
      }
    };

    fetchUser();
  }, [userId]);

  // Handle input changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setUpdatedUser((prevUser) => ({
      ...prevUser,
      [name]: value,
    }));
  };

  // Submit updated data to the backend
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:5000/api/auth/update-profile", updatedUser);
      setUser(updatedUser);
      setIsEditing(false);
      console.log("User updated successfully");
    } catch (error) {
      console.error("Error updating user:", error);
    }
  };

  // Toggle editing mode
  const toggleEdit = () => setIsEditing(!isEditing);

  if (!user) return <p>Loading user data...</p>;

  return (
    <div>
      <h1>User Profile</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Name:</label>
          <input
            type="text"
            name="name"
            value={updatedUser.name}
            onChange={handleChange}
            disabled={!isEditing}
          />
        </div>
        <div>
          <label>ID:</label>
          <input
            type="text"
            name="id"
            value={updatedUser.user_id}
            onChange={handleChange}
            disabled
          />
        </div>
        <div>
          <label>Password:</label>
          <input
            type="password"
            name="password"
            value={updatedUser.password}
            onChange={handleChange}
            disabled={!isEditing}
          />
        </div>
        <div>
          <label>Email:</label>
          <input
            type="text"
            name="email"
            value={updatedUser.email}
            onChange={handleChange}
            disabled={!isEditing}
          />
        </div>
        <div>
          <label>Phone Number:</label>
          <input
            type="tel"
            name="phone"
            value={updatedUser.phone}
            onChange={handleChange}
            disabled={!isEditing}
          />
        </div>
        {isEditing ? (
          <button type="submit">Save Changes</button>
        ) : (
          <button type="button" onClick={toggleEdit}>
            Update Profile
          </button>
        )}
      </form>
    </div>
  );
}

export default UpdateUserProfile;
